DROP DATABASE IF EXISTS GeoJobSearch;

CREATE DATABASE GeoJobSearch CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

USE GeoJobSearch;

DROP TABLE IF EXISTS `Jobs`;
DROP TABLE IF EXISTS `Company`;
DROP TABLE IF EXISTS `Users`;

CREATE TABLE `Company` (
    CompanyUID VARCHAR(255) NOT NULL,
    Name VARCHAR(255) NOT NULL,
    City VARCHAR(255) NOT NULL,
    Province VARCHAR(255) NOT NULL,
    Address VARCHAR(255),
    Latitude DECIMAL(10, 8),
    Longitude DECIMAL(11, 8),
    PRIMARY KEY (CompanyUID)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE `Users` (
    UserID INT AUTO_INCREMENT PRIMARY KEY,
    Username VARCHAR(255) NOT NULL,
    Email VARCHAR(255) NOT NULL,
    HashedPassword VARCHAR(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE `Jobs` (
    JobID INT AUTO_INCREMENT,
    JobUID VARCHAR(255) NOT NULL,
    Title VARCHAR(255) NOT NULL,
    Company VARCHAR(255) NOT NULL,
    CompanyUID VARCHAR(255),
    Location VARCHAR(255) NOT NULL,
    City VARCHAR(255) NOT NULL,
    Province VARCHAR(255) NOT NULL,
    Description TEXT,
    Salary INT,
    JobType VARCHAR(255) NOT NULL,
    Date DATE,
    JobURL TEXT,
    PRIMARY KEY (JobUID),
    UNIQUE (JobID),
    FOREIGN KEY (CompanyUID) REFERENCES Company(CompanyUID)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;



